mapper.select:
select board_no ,board_id ,  board_title ,board_content, board_date , board_views
from board
WHERE BOARD_NO = #{board_no}

mapper.update:
UPDATE BOARD
SET BOARD_TITLE = #{board_title} , BOARD_CONTENT = #{board_title} 
WHERE BOARD_NO = #{board_no}

UPDATE BOARD
SET BOARD_VIEWS = BOARD_VIEWS +1 
WHERE BOARD_NO = #{BOARD_NO}
